using System.Windows;
using System.Windows.Controls;

/// <summary>
/// $PANELNAME$
/// </summary>
public class $PANELNAME$ : Panel
{
    // The MeasureOverride method is your chance to ask
    // all of your children how big they want to be

    protected override Size MeasureOverride(Size availableSize)
    {
        Size YouCanBeAsBigAsYouWant = new Size(double.PositiveInfinity, double.PositiveInfinity);

        foreach (UIElement element in this.Children)
        {
            element.Measure(YouCanBeAsBigAsYouWant);
        }

        return new Size();
    }

    // The ArrangeOverride method is your chance to place
    // and size all of your children

    protected override Size ArrangeOverride(Size finalSize)
    {
        double top = 0;
        double left = 0;
        double width = 0;
        double height = 0;

        foreach (UIElement element in this.Children)
        {
            // YOUR CODE HERE
            // To change where the element get placed, just modify any of the values below.
            $END$
            
            width = element.DesiredSize.Width;
            height = element.DesiredSize.Height;

            element.Arrange(new Rect(left, top, width, height));

            top = top + 100;
            left = left + 100;
        }

        return finalSize;
    }
}
